import logo from './logo.svg';
import './App.css';

function App() {

  let testname = '클래스네임';
  let posts = '강남 고기 맛집';
  let blue30px = { color:'blue', fontSize:'30px'}

  function 함수(){
    return 100
  }


  return (
    <div className="App">
      <div className="black-nav">
        <div style={ blue30px }>개발 Blog 따라하기</div>
      </div>
      <div className={ testname }>
        <ul style={ {listStyle:'none', textAlign:'left'} }>
          <li>JSX문법 (J</li>
          <li>1. 중괄호로 값을 바인딩 할 수 있다.</li>
          <li>2. className 에 let 변수명을 꽂아넣을 수 있다.</li>
          <li>이 div의 classname 이름은 변수'testname' 이고 값은 '클래스네임'이다.</li>
          <li>3. style 값을 중괄호로 묶어서 저장할 수도 있다.</li>
          <li>4. img src 도 js 파일 최상단에서 import 선언한 이름값을 통해 가져올 수 있다.</li>
        </ul>


        <br/>
        <br/>

      </div>
      {/* <img src={ logo }/> */}
      <h4> { posts } </h4>
      <h4> { 함수() } </h4>
      <div className="list">
        <h3> { posts } </h3>
        <p>1월 24일 발행</p>
        <hr/>
      </div>
    </div>
  );
}

export default App;
