import React, {useState} from 'react';

function InputSample () {

    const [text, setText] = useState('');

    const onChange =(e) =>{
        setText(e.target.value);
    }

    return (
        <>
            <input/>
            <button onClick={onChange}>초기화</button>
            <div>
                <b>값 : {text}</b>
            </div>
        </>
    );
}

export default InputSample;
